$( Introduce new constant symbols. $)
$c :  $.
$( Colon $)
$c Fun  $.
$( Function predicate $)
$c Fn  $.
$( Function connective $)
$c -->  $.
$( Domain-codomain connective $)
$c -1-1->  $.
$( 'One-to-one' domain-codomain connective $)
$c -onto->  $.
$( 'Onto' domain-codomain connective $)
$c -1-1-onto->  $.
$( 'One-to-one' and 'onto' domain-codomain connective $)
